#include <stdio.h>

int main() {
    int n;
    printf("Enter the value of n: ");
    scanf("%d", &n);

    int fib = 0;
    if (n == 1 || n == 2) {
        fib = 1;
    } else {
        int first = 1;
        int second = 1;
        for (int i = 3; i <= n; i++) {
            fib = first + second;
            first = second;
            second = fib;
        }
    }
    printf("The %dth Fibonacci number is %d\n", n, fib);
    return 0;
}

