def fib(n):
    if n == 0:
        return 0
    if n == 1:
        return 1
    fib_cache = [0 for _ in range(n)]
    fib_cache[0] = 0
    fib_cache[1] = 1
    for i in range(2, n):
        fib_cache[i] = fib_cache[i-1] + fib_cache[i-2]
    return fib_cache[n-1]

