#include <fstream>
#include <iostream>
#include <string>
using namespace std;

int main() {
    ifstream file("example.txt");
    string word;
    string line;
    int count = 0;

    cout << "Введите слово для поиска: ";
    cin >> word;

    while (getline(file, line)) {
        if (line.find(word) != string::npos) {
            count++;
        }
    }
    cout << "Количество вхождений слова '" << word << "' в файле: " << count << endl;
}

