#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main() {
    // Tên tệp
    string filename = "input.txt";
    // Từ cần đếm xuất hiện
    string word = "example";
    // Biến đếm xuất hiện
    int count = 0;
    // Đọc tệp văn bản theo dòng và lưu nó vào một chuỗi string
