    string line;
    while (getline(file, line)) {
        // Tìm kiếm từ trong dòng đọc được
        if (line.find(word) != string::npos) {
            count++; // Tăng biến đếm xuất hiện lên 1
        }
    }
    file.close(); // Đóng tệp sau khi xử lý xong nội dung của nó
    cout << "Từ '" << word << "' xuất hiện " << count << " lần." << endl; // In kết quả tại console, cho biết số lần xuất hiện của từ trong tệp văn bản
}
