```
#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::ifstream file("path/to/file.txt");
    std::string word;
    int count = 0;
    while (file >> word) {
        if (word == "the") {
            count++;
        }
    }
    std::cout << "The word 'the' appeared " << count << " times." << std::endl;
}
