int main() {
    // Määrittele tietty sana, jonka lukumäärä haluat laskea tiedostossa
    std::string wordToCount = "esimerkki";

    // Luo tiedostolukijaluokka ja avaa tiedosto "my_text_file.txt" lukuun
    std::ifstream file("my_text_file.txt");

    // Laske tietty sanan lukumäärä tiedostossa ja lisää sen muuttujaan "count"
    int count = 0;
    std::string line;
    while (getline(file, line)) {
        if (line.find(wordToCount) != std::string::npos) {
            count++;
        }
    }

    // Tulosta tietty sanan lukumäärä tiedostossa konsolille
    std::cout << "Tiedostossa '" << wordToCount << "' sanaa on " << count << " kertaa." << std::endl;
}
