    if (n <= 1) {
        printf("The %dth Fibonacci number is %d\n", n, n);
        return 0;
    }

    int fib1 = 0, fib2 = 1, fib3 = 1;
    for (int i = 3; i <= n; i++) {
        fib3 = fib1 + fib2;
        fib1 = fib2;
        fib2 = fib3;
    }

    printf("The %dth Fibonacci number is %d\n", n, fib3);
    return 0;
}

