```
#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main() {
    // Đọc tên tệp từ dòng lệnh
    string filename;
    cout << "Nhập tên tệp: ";
    cin >> filename;

    // Kiểm tra xem tệp có tồn tại không
    ifstream file(filename);
