#include <iostream>
#include <cmath>
using namespace std;

int fibonacci(int n) {
    if (n == 0) {
        return 0;
    } else if (n == 1) {
        return 1;
    } else {
        int previous_num = 1;
        int current_num = 1;
        for (int i = 2; i <= n; i++) {
            int temp = current_num + previous_num;
            previous_num = current_num;
            current_num = temp;
        }
        return current_num;
    }
}

