def fib(n):
    if n == 0 or n == 1:
        return n
    else:
        return fib(n - 1) + fib(n - 2)

# Dynaaminen ohjelmointi
memo = {}
def fib_dynamic(n):
    if n in memo:
        return memo[n]
    else:
        value = fib_dynamic(n - 1) + fib_dynamic(n - 2) if n > 1 else n
        memo[n] = value
        return value

