# इन्क्लूड आवश्यक हेडर फ़ाइल

#include <fstream>
#include <string>
#include <iostream>

# दृश्यात्मक सुरक्षा के लिए यह विशिष्ट शब्द मुख्यीय तरीके से फ़ाइल में खोजा जाता है
const std::string keyword = "the";

int main() {
    // पाठ फ़ाइल खोलें
    std::ifstream file("path/to/file.txt");
    if (!file.is_open()) {
        std::cout << "पाठ फ़ाइल खोलने में असुरक्षित";
        return -1;
    }

    // पूरी पाठ फ़ाइल पढ़ें
    std::string line;
    while (std::getline(file, line)) {
        // "the" आवरणीय।
        if (line.find(keyword) != std::string::npos) {
            std::cout << "आवरणीय 'the' : " << line << std::endl;
        } else {
            std::cout << "अ-आवरणीय : " << line << std::endl;
        }
    }
}

