def fibonacci(n):
    if n == 0 or n == 1:
        return n
    else:
        cache = {0: 0, 1: 1}
        def helper(k):
            if k in cache:
                return cache[k]
            else:
                result = helper(k - 1) + helper(k - 2)
                cache[k] = result
                return result
        return helper(n)

