def fibonacci(n):
    if n == 0:
        return 0
    elif n == 1:
        return 1
    else:
        fib = [0, 1] # 初始化数组为前两个斐波那契数
        for i in range(2, n+1): # 从第三个斐波那契数开始，遍历到第n个斐波那契数
