#include <fstream>
#include <string>
#include <iostream>

int main() {
  std::ifstream file("path/to/file.txt");
  std::string word;
  int count = 0;

  if (file.is_open()) {
    while (file >> word) {
      if (word == "specific_word") {
        count++;
      }
    }
    file.close();
  } else {
    std::cout << "Error opening file." << std::endl;
    return 1;
  }
  std::cout << "The word \"specific_word\" appeared " << count << " times." << std::endl;
}
