#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string mot_cherche = "cherche"; // mot à rechercher dans le fichier
    string fichier = "fichier.txt"; // nom du fichier
    int occurrences = 0; // nombre d'occurrences trouvées du mot cherche dans le fichier
    ifstream file(fichier); // ouvrir le fichier en lecture seule
    if (!file) { // si l'ouverture du fichier a échoué
        cout << "Impossible d'ouvrir le fichier" << endl;
        return -1;
    } else { // sinon, lire ligne après ligne depuis le fichier en fonction des caractères qu'il contient, jusqu'à la fin du fichier inclus. 
        while (file.good()) { // filer les données au standard output en fonction de chaque ligne lue. 
            string ligne = file.get(); // lire une seule ligne depuis le fichier en fonction de chaque caractère qu'il contient. 
            if (ligne.find(mot_cherche) != std::string::npos) { // si la recherche a trouvé un résultat, incrementer le nombre d'occurrences trouvées de la valeur cherche. 
                occurrences++; 
            } 
            cout << ligne << endl; // afficher la ligne lue avec tous les caractères qu'elle contient, y compris la valeur cherche. 
        } 
    } 
    file.close(); // fermer la lecture seule du fichier après avoir lu toutes les données qu'il contient. 
    cout << "Le mot cherche a été trouvé " << occurrences << " fois dans le fichier." << endl; // afficher les informations sur l'état de la recherche et son résultat. 
} 
```

