#include <fstream>
#include <iostream>
#include <string>
#include <cstring>

int main() {
  std::ifstream file("sample.txt"); // Öffnet den angegebenen Textdatei
  int counter = 0; // Anzahl der Vorkommnisse des Wortes
  std::string line; // Zeile aus der Datei
