    board/.style={draw, thick, rounded corners=3pt},
    square/.style={draw, fill=black, circle, inner sep=1pt},
]
\foreach \i in {1,...,\the\numexpr8+1} {
    \foreach \j in {1,...,\the\numexpr8+1} {
        \node[board] at (\i,\j) {};
    }
}
\end{tikzpicture}
\end{code}

