#include <iostream>
#include <fstream>
#include <string>
#include <cstdlib>

using namespace std;

int main() {
    string line;
    ifstream myfile("file.txt");
    int count = 0;
    while(getline(myfile, line)) {
        if (line.find("word") != std::string::npos) {
            count++; // count the number of words that match "word" in the file.txt file.
        }
    }
    cout << count << endl; // print the number of times "word" was found in the file.txt file.
}
