int main() {
    // Declare map to store the number of times each word occurs in the file
    map<string, int> word_counts;
    // Open the file for reading and use a loop to read each line from it
    ifstream file("input_file.txt");
    string line;
    while (getline(file, line)) {
        // Split the line by spaces and add each word to a vector
        vector<string> words = split(line, " ");
