            // Increment the word count if the word exists in the map and if it is not already present in the map then add it as a key value pair to the map with 1 as its value
            if (word_counts.find(word) == word_counts.end()) {
                word_counts[word] = 1;
            } else {
                ++word_counts[word];
            }
        }
    }
    // Iterate over all words in the map and display the number of times each word occurs in the file
    for (auto& [key, value] : word_counts) {
        cout << key << " appears " << value << " times." << endl;
    }    
}
