\begin{chessboard}
f {rnbqkb1r/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1}
\end{chessboard}
% Switch back to the first chessboard package to create the board.
\begin{chessboard}
f {rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1}
\end{chessboard}
% Switch back to the second chessboard package to create the board.
