function fib(n) {
  if (n === 0) return 0;
  if (n === 1) return 1;
  var prev = 0;
  var curr = 1;
  for (var i = 2; i <= n; i++) {
    var temp = prev + curr;
    prev = curr;
    curr = temp;
  }
  return curr;
}
