#include <iostream>
#include <fstream>
#include <string>
#include <map>
using namespace std;

int main() {
    // 定义一个文件名和单词映射数组 map<string, int> 用于存储单词出现次数。
    string filename = "input.txt";
    map<string, int> words;

    // 打开文件，并开始逐行读取文件。
    ifstream infile(filename);
    if (!infile.is_open()) {
        cerr << "Unable to open file" << endl;
        return 1;
    }

    // 逐行读取文件，并计算单词出现次数。
    while (infile) {
        string line = infile.getline(); // 获取当前行。
        int wordIndex = line.find(" "); // 查找单词索引。
        string word = line.substr(0, wordIndex); // 获取单词字符串。
        int count = words.count(word); // 计算单词出现次数。
        if (count == 0) { // 如果没有记录，则添加到 map 中。
            words[word] = 1; // 设置为 1。
        } else { // 如果已经存在记录，则更新其值。
            words[word] += count; // 更新其值为当前值加上新的值。            
        }            
    }    

    // 关闭文件。
    infile.close();  

    // 输出单词和出现次数的映射数组。    
    for (auto& entry : words) { // for each word and its count in the map entry ...    
        cout << entry.first << ": " << entry.second << endl;    
    }    
}

