```c++
#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
    string textFilePath = "inputFile.txt"; // Ersätt med din egen filnamn.
    vector<string> words; // En container för varje ord i filen.
    vector<int> count; // En container för antalet förekomster av varje ord.
    ifstream inputFile(textFilePath); // Öppnar filen.
    if (!inputFile) { // Kontrollerar om filen inte är tom eller om läsningen misslyckades.
        cerr << "Could not open the file: " << textFilePath << endl;
        return 1;
    }
    string line; // Den aktuella raden.
    while (getline(inputFile, line)) { // Läs den aktuella raden från filen.
        if (line.find("word_to_count") != string::npos) { // Kontrollerar om ordet "word_to_count" finns i raden. 
            words = split(line, " "); // Skiljer på radens värden med split och returnerar dem i en container med en array (för enkelhetens skull). 
            for (int i = 0; i < words.size(); i++) { // Cyklar genom varje ord i containern och uppdaterar antalet förekomster. 
                if (words[i] == "word_to_count") { // Kontrollerar om ordet är lika med det du vill räkna förekomsten av. 
                    count[i]++; // Uppdaterar count-arrayet med ett antal förekomster för det aktuella ordet. 
                } 
            } 
        } 
    } 
    inputFile.close(); // Sluter läsningen från filen. 
    for (int i = 0; i < words.size(); i++) { // Cyklar genom varje ord i containern och visar upp informationen. 
        cout << words[i] << ": " << count[i] << endl; 
    } 
}

