#include <fstream>
#include <string>
#include <iostream>
using namespace std;

int main() {
    ifstream file("input.txt"); // 读取文件“input.txt”
    string word;
    int count = 0; // 记录单词出现次数
    while (getline(file, word)) { // 逐行从文件中读取单词
        if (word == "dog") { // 如果单词为“dog”
