#include <iostream>
using namespace std;

int fib(int n) {
    // create an array to store the Fibonacci numbers
    int fibs[n+1];
    // set the first two values of the array
    fibs[0] = 0;
    fibs[1] = 1;
    // use dynamic programming to fill in the rest of the array
    for (int i = 2; i <= n; i++) {
        fibs[i] = fibs[i-1] + fibs[i-2];
    }
    // return the n-th Fibonacci number
    return fibs[n];
}

int main() {
    int n;
    cin >> n;
    cout << "The " << n << "-th Fibonacci number is " << fib(n) << endl;
    return 0;
}

