```c++
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
  string word; // la palabra a buscar
  string line; // la linea actual
  int count = 0; // contador

  // Inicializar la palabra a buscar
  cout << "Ingrese la palabra a buscar: ";
  cin >> word;

  // Abrir el archivo de texto
  ifstream file("archivo.txt");

  // Leer línea por línea del archivo
  while (getline(file, line)) {
    // Buscar la palabra en la línea actual
    size_t pos = line.find(word);
    while (pos != string::npos) {
      count++;
      pos = line.find(word, pos + word.length());
    }
  }

  // Mostrar el resultado
  cout << "La palabra \"" << word << "\" aparece " << count << " veces en el archivo." << endl;

  return 0;
}
```

