#include <iostream>
#include <vector>
using namespace std;

vector<int> findCommonElements(const vector<int>& arr1, const vector<int>& arr2) {
    vector<int> result;

    // Sort both arrays in ascending order
    sort(arr1.begin(), arr1.end());
    sort(arr2.begin(), arr2.end());

    // Initialize pointers to the first elements of both arrays
    int i = 0, j = 0;

    // Loop until both pointers reach the end of their respective arrays
    while (i < arr1.size() && j < arr2.size()) {
        // If the current elements are equal, add it to the result and increment both pointers
        if (arr1[i] == arr2[j]) {
            result.push_back(arr1[i]);
            i++;
            j++;
        }

        // If the current element of arr1 is smaller than the current element of arr2, increment i
        else if (arr1[i] < arr2[j]) {
            i++;
        }

        // If the current element of arr2 is smaller than the current element of arr1, increment j
        else {
            j++;
        }
    }

    return result;
}

int main() {
    vector<int> arr1{1, 3, 4, 5, 7};
    vector<int> arr2{3, 4, 5, 6, 8};

    vector<int> commonElements = findCommonElements(arr1, arr2);

