```
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // The word to count
    int count = 0; // The word count

    cout << "Enter the word to count: ";
    cin >> word;

    // Open the file
    ifstream file("text_file.txt");

    // Read each line of the file
    string line;
    while (getline(file, line)) {
        // Split the line into words
        string words[100];
        int word_count = 0;
        string::size_type start = 0;
        string::size_type end = 0;
        do {
            end = line.find(' ', start);
            words[word_count] = line.substr(start, end - start);
            start = end + 1;
            word_count++;
        } while (end != string::npos);

        // Check if the word is in the list of words
        bool found = false;
        for (int i = 0; i < word_count; i++) {
            if (words[i] == word) {
                found = true;
                break;
            }
        }

        // Increment the count if the word is found
        if (found) {
            count++;
        }
    }

    // Print the result
    cout << "The word " << word << " appears " << count << " times in the file." << endl;

    return 0;
}
```
