#include <iostream>
using namespace std;

int fibonacci(int n) {
    int f[n+1];
    f[0] = 0;
    f[1] = 1;
    for (int i=2; i<=n; i++) {
        f[i] = f[i-1] + f[i-2];
    }
    return f[n];
}

int main() {
    int n;
    cout << "Enter the number of the Fibonacci number you want to find: ";
    cin >> n;
    int result = fibonacci(n);
    cout << "The " << n << "th Fibonacci number is " << result << endl;
    return 0;
}
```

