```c++
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
  string word; // Λέξη που αναζητάμε
  int count = 0; // Αριθμός εμφανίσεων της λέξης

  cout << "Δώστε τη λέξη που θέλετε να αναζητήσετε: ";
  cin >> word;

  // Ανοίγουμε το αρχείο
  ifstream file("input.txt");
  if (file.is_open()) {
    string line;

    // Διαβάζουμε γραμμή προς γραμμή το αρχείο
    while (getline(file, line)) {
      size_t pos = line.find(word);

      // Εάν βρεθεί η λέξη στην γραμμή, προσθέτουμε 1 στο count
      if (pos != string::npos) {
        count++;
      }
    }

    file.close();

    cout << "Ο αριθμός εμφανίσεων της λέξης '" << word << "' είναι " << count << endl;
  } else {
    cout << "Αρχείο δεν βρέθηκε!" << endl;
  }

  return 0;
}
```

