#include <iostream>
#include <fstream>
#include <string>

int main() {
  std::ifstream file("path/to/file.txt");

  if (!file.is_open()) {
    std::cerr << "Error al abrir el archivo." << std::endl;
    return 1;
  }

  std::string word;
  std::cout << "Introduzca la palabra a buscar: ";
  std::cin >> word;

  int count = 0;

  while (file) {
    std::string line;
    getline(file, line);
    if (line.find(word) != std::string::npos) {
      count++;
    }
  }

  file.close();

  std::cout << "La palabra '" << word << "' aparece " << count
            << " veces en el archivo." << std::endl;

  return 0;
}
