import java.util.ArrayList;

public class Fibonacci {
    public static int fibonacci(int n) {
        if (n <= 1) {
            return n;
        }

        ArrayList<Integer> memo = new ArrayList<>();
        memo.add(0);
        memo.add(1);
        for (int i = 2; i < n; i++) {
            memo.add(memo.get(i - 1) + memo.get(i - 2));
        }

        return memo.get(n - 1);
    }
}

