        while (pos != string::npos) {
            wordCount++;
            pos = line.find(word, pos + 1);
        }
    }

    // Close the input file
    infile.close();

    // Print the number of times the word appears in the file
    cout << "The word appears " << wordCount << " times in the file." << endl;
    return 0;
}
