#include <iostream>

int fibonacci(int n) {
    if (n == 0 || n == 1) {
        return n;
    }

    return fibonacci(n - 1) + fibonacci(n - 2);
}

int main() {
    int n;
    std::cout << "Adj meg egy számot: ";
    std::cin >> n;

    int result = fibonacci(n);
    std::cout << "A " << n << ". Fibonacci-szám: " << result << std::endl;

    return 0;
}
```

