#include <iostream>
using namespace std;

// Funktoren for å finne det n-te Fibonacci-tallet
int fib(int n) {
    // Tabelle for lagring av resultatet
    int dp[n+1];
    dp[0] = 0;
    dp[1] = 1;

    // Beregn alle Fibonacci-tallene
    for (int i = 2; i <= n; i++) {
        dp[i] = dp[i-1] + dp[i-2];
    }

    // Returnerer det n-te Fibonacci-tallet
    return dp[n];
}

// Hovedfunksjonen
int main() {
    int n;

    // Les inn input-verdi
    cin >> n;

    // Beregn og skriv ut det n-te Fibonacci-tallet
    cout << fib(n) << endl;

    return 0;
}
```

