#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // Khai báo biến word để lưu từ cần tìm
    int count = 0; // Khai báo biến count để lưu số lần xuất hiện của từ

    // Nhập từ cần tìm
    cout << "Nhap tu can tim: ";
    cin >> word;

    // Mở file văn bản và đọc từng dòng
    ifstream file("input.txt");
    string line;
    while (getline(file, line)) {
        size_t pos = line.find(word);
        while (pos != string::npos) {
            count++;
            pos = line.find(word, pos + word.length());
        }
    }

    // In kết quả
    cout << "So lan xuat hien cua tu " << word << " la: " << count << endl;

    return 0;
}

