#include <stdio.h>

int fib(int n) {
  static int a = 0, b = 1;

  if (n == 0) return a;
  else if (n == 1) return b;
  else {
    int temp = b;
    b = a + b;
    a = temp;
    return fib(n - 1);
  }
}

int main() {
  int n;
  printf("Enter n: ");
  scanf("%d", &n);
  printf("Fibonacci number %d is %d\n", n, fib(n));
  return 0;
}
