```c++
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // palabra a buscar
    int count = 0; // contador de apariciones
    ifstream file("file.txt"); // archivo a leer
    if (file.is_open()) {
        while (file.good()) {
            string line;
            getline(file, line);
            size_t pos = line.find(word);
            while (pos != string::npos) {
                count++;
                pos = line.find(word, pos + word.length());
            }
        }
        file.close();
        cout << "La palabra " << word << " aparece " << count << " veces en el archivo." << endl;
    } else {
        cout << "No se pudo abrir el archivo." << endl;
    }
    return 0;
}
```

