#include <iostream>
#include <fstream>
#include <string>

int main() {
  std::string word; // Η λέξη που θα αναζητάμε
  std::string line; // Η γραμμή του αρχείου που διαβάζουμε

  // Ζητά από το χρήστη να εισαγάγει τη λέξη που θέλει να αναζητήσει
  std::cout << "Δώστε τη λέξη που θέλετε να αναζητήσετε: ";
  std::cin >> word;

  // Δημιουργούμε ένα αντικείμενο ifstream για να ανοίξουμε το αρχείο για ανάγνωση
  std::ifstream inputFile("file.txt");

  // Μεταβλητής για τον αριθμό των εμφανίσεων της λέξης στο αρχείο
  int count = 0;

  // Διαβάζουμε κάθε γραμμή του αρχείου και μετράμε τον αριθμό των εμφανίσεων της λέξης
  while (getline(inputFile, line)) {
    size_t pos = line.find(word);
    while (pos != std::string::npos) {
