```c++
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
  string word;
  int wordCount = 0;

  // Lue käyttäjältä hakusana
  cout << "Anna hakusana: ";
  cin >> word;

  // Avaa tekstitiedosto
  ifstream file("filename.txt");

  // Lue tiedostosta rivi riviltä
  string line;
  while (getline(file, line)) {
    // Etsi hakusanaa riviltä
    size_t pos = line.find(word);
    while (pos != string::npos) {
      wordCount++;
      pos = line.find(word, pos + word.length());
    }
  }

  // Tulosta hakusanatiedot
  cout << "Hakusanan " << word << " määrä: " << wordCount << endl;

  return 0;
}
```

