#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string mot_a_rechercher;
    cout << "Saisissez le mot à rechercher : ";
    cin >> mot_a_rechercher;

    int nb_occurences = 0;
    string ligne;
    ifstream fichier("nom_fichier.txt");
    while (getline(fichier, ligne)) {
        size_t position = 0;
        while ((position = ligne.find(mot_a_rechercher, position)) != string::npos) {
            nb_occurences++;
            position += mot_a_rechercher.length();
        }
    }

    cout << "Le mot " << mot_a_rechercher << " apparait " << nb_occurences << " fois dans le fichier." << endl;
    return 0;
}

