using namespace std;

int main() {
    string word;
    unordered_map<string, int> word_counts;

    ifstream input_file("file.txt");
    while (input_file >> word) {
        if (word_counts.find(word) == word_counts.end()) {
            word_counts[word] = 1;
        } else {
            word_counts[word]++;
        }
    }
