```c++
#include <stdio.h>
#include <stdlib.h>
#define MAX 100

int fibonacci(int n) {
    int fib[MAX];
    fib[0] = 0;
    fib[1] = 1;

    for (int i = 2; i <= n; i++) {
        fib[i] = fib[i - 1] + fib[i - 2];
    }

    return fib[n];
}

int main() {
    int n;
    printf("Enter the value of n: ");
    scanf("%d", &n);

    printf("Fibonacci of %d is %d\n", n, fibonacci(n));

    return 0;
}
```

