```cpp
#include <iostream>
using namespace std;

int main() {
    int arr1[] = {1, 2, 3, 4, 5};
    int arr2[] = {2, 3, 6, 7, 8};
    int n1 = sizeof(arr1) / sizeof(arr1[0]);
    int n2 = sizeof(arr2) / sizeof(arr2[0]);
    int count = 0;
    for (int i = 0; i < n1; i++) {
        for (int j = 0; j < n2; j++) {
            if (arr1[i] == arr2[j]) {
                count++;
                break;
            }
        }
    }
    int arr3[count];
    for (int i = 0, j = 0; i < n1 && j < count; i++) {
        for (int k = 0; k < n2; k++) {
            if (arr1[i] == arr2[k]) {
                arr3[j++] = arr1[i];
                break;
            }
        }
    }
    cout << "Cac phan tu chung la: ";
    for (int i = 0; i < count; i++) {
        cout << arr3[i] << " ";
    }
    return 0;
}
```

