#include <stdio.h>

int main()
{
    int arr1[] = {1, 2, 3, 4, 5};
    int arr2[] = {4, 5, 6, 7, 8};
    int common_elements[5];
    int i, j, k = 0;

    for (i = 0; i < sizeof(arr1) / sizeof(arr1[0]); i++) {
        for (j = 0; j < sizeof(arr2) / sizeof(arr2[0]); j++) {
            if (arr1[i] == arr2[j]) {
                common_elements[k++] = arr1[i];
                break;
            }
        }
    }

    for (i = 0; i < k; i++) {
        printf("%d ", common_elements[i]);
    }

    return 0;
}
```
