import numpy as np
import random

def create_sudoku(size=9):
    # We are creating a square sudoku
    assert size**2 % 3 == 0, 'Size must be a square number that is a multiple of 3'

    # Create a matrix of zeros of the given size
    sudoku = np.zeros((size, size))

    # Create the initial set of possible values for each cell
    for i in range(size):
        for j in range(size):
            sudoku[i][j] = list(range(1, size + 1))

    # Perform the sudoku algorithm to get the solution
    while True:
        # Validate the current state of the sudoku
        if validate_sudoku(sudoku):
            break

        # Randomly change a cell
        x, y = random.randint(0, size - 1), random.randint(0, size - 1)
        possible_values = sudoku[x][y]

        if len(possible_values) == 1:
            continue

        new_value = random.choice(possible_values)
        sudoku[x][y] = [new_value]

    return sudoku

def validate_sudoku(sudoku):
    size = len(sudoku)
    for i in range(size):
        # Validate rows
        if not validate_row(sudoku[i]):
            return False

        # Validate columns
        if not validate_row(sudoku[:, i]):
            return False

        # Validate square
        for x in range(0, size, size // 3):
            for y in range(0, size, size // 3):
                if not validate_square(sudoku[x:x + (size // 3), y:y + (size // 3)]):
                    return False

    return True

def validate_row(row):
    return len(set(row)) == len(row)

def validate_square(square):
    return len(set([value for row in square for value in row])) == len(square)

if __name__ == '__main__':
    sudoku = create_sudoku()
    print(sudoku)
```

