#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string wordToCount;
    std::cout << "Bitte geben Sie das Wort ein, das Sie zählen möchten: ";
    std::cin >> wordToCount;

    std::ifstream file("dateiname.txt");
    int count = 0;
    std::string line;
    while (std::getline(file, line)) {
        std::istringstream iss(line);
        std::string word;
        while (iss >> word) {
            if (word == wordToCount) {
