#include <iostream>
#include <fstream>
#include <string>
#include <vector>

int main() {
  // Luo tiedosto, joka sisältää halutun sanan
  std::string filename = "input.txt";
  std::ifstream infile(filename);

  // Tallenna sanat listaksi
  std::vector<std::string> words;
  std::string line;
  while (std::getline(infile, line)) {
    // Hae sanat riveistä
    std::istringstream iss(line);
    std::string word;
    while (iss >> word) {
      words.push_back(word);
    }
  }

  // Laske sanat muihin tietorakenteisiin tallennettuna
  int count = 0;
  for (auto& word : words) {
    if (word == "sanasi") {
      count++;
    }
  }

  // Tulosta lopputulokset
  std::cout << "Sanasi esiintyy " << count << " kertaa." << std::endl;

  return 0;
}

